        city: "",
        website: "",
    })
    const upload = async (file) => {
        try {
            const formData = new FormData();
            formData.append("file", file);
            const res = await makeRequest.post("/upload", formData);
            return res.data;
        } catch (error) {
            console.log(error);
        }

    }
    const handleChange = (e) => {

        setTexts((prev) => ({ ...prev, [e.target.name]: [e.target.value] }));
