import "./profile.scss";
import FacebookTwoToneIcon from "@mui/icons-material/FacebookTwoTone";
import LinkedInIcon from "@mui/icons-material/LinkedIn";
import InstagramIcon from "@mui/icons-material/Instagram";
import PinterestIcon from "@mui/icons-material/Pinterest";
import TwitterIcon from "@mui/icons-material/Twitter";
import PlaceIcon from "@mui/icons-material/Place";
import LanguageIcon from "@mui/icons-material/Language";
import EmailOutlinedIcon from "@mui/icons-material/EmailOutlined";
import MoreVertIcon from "@mui/icons-material/MoreVert";
import Posts from "../../components/posts/Posts"
import {
  useQuery,
  useQueryClient

} from "@tanstack/react-query";
import { AuthContext } from "../../context/authContext.js";
import { makeRequest } from "../../axios.js";
import { useContext } from "react";
import { useLocation } from "react-router-dom";

const Profile = () => {
  const queryClient = useQueryClient();

  const { currentUser } = useContext(AuthContext);
  const userId = parseInt(useLocation().pathname.split("/")[2]);
  const { isLoading, error, data } = useQuery({
    queryKey: ["users"],
    queryFn: async () => ((await makeRequest.get("/users/find/" + userId)).data),
  });
  const { rIsLoading, relationshipData } = useQuery({
    queryKey: ["relationship"],
    queryFn: async () => ((await makeRequest.get("/relationships?followedUserId=" + userId)).data),
  });
  console.log(relationshipData);
  console.log("HEERererreEEEEEEEEEEEEEEEEEEE")
  return (

    <div className="profile">
      {
        isLoading ? "Loading" : <>
          <div className="images">
            <img
              src={data?.coverPic}
              alt=""
              className="cover"
            />
            <img
              src={data?.profilePic}
              alt=""
              className="profilePic"
            />
          </div>
          <div className="profileContainer">
            <div className="uInfo">
              <div className="left">
                <a href="http://facebook.com">
                  <FacebookTwoToneIcon fontSize="large" />
                </a>
                <a href="http://facebook.com">
                  <InstagramIcon fontSize="large" />
                </a>
                <a href="http://facebook.com">
                  <TwitterIcon fontSize="large" />
                </a>
                <a href="http://facebook.com">
                  <LinkedInIcon fontSize="large" />
                </a>
                <a href="http://facebook.com">
                  <PinterestIcon fontSize="large" />
                </a>
              </div>
              <div className="center">
                <span>{data?.name}</span>
                <div className="info">
                  <div className="item">
                    <PlaceIcon />
                    <span>{data?.city}</span>
                  </div>
                  <div className="item">
                    <LanguageIcon />
                    <span>{data?.website}</span>
                  </div>
                </div>
                {rIsLoading ? "Loading" : currentUser.id === userId ?
                  <button>Update profile</button>
                  :
                  <button>{relationshipData.includes(currentUser.id) ? "Following" : "follow"}</button>

                }
              </div>
              <div className="right">
                <EmailOutlinedIcon />
                <MoreVertIcon />
              </div>
            </div>
            {/* <Posts /> */}
          </div>
        </>}  </div>
  );
};

export default Profile;
